import React, { useEffect, useState } from "react";
import { View, Text, StyleSheet, Button } from "react-native";
import { TouchableOpacity } from "react-native-gesture-handler";
import { useSelector, useDispatch } from "react-redux";
import { RootState } from "../reducers";
import MapView, { PROVIDER_GOOGLE, Marker } from "react-native-maps";

interface MarkerData {
  coords: {
    latitude: number;
    longitude: number;
  };
  title: string;
  description: string;
  pinColor: string;
}

interface CenterPointZoom {
  latitude: number;
  longitude: number;
  latitudeDelta: number;
  longitudeDelta: number;
}

const MapPage = () => {
  // useEffect(() => {
  //   // App.tsx ???
  //   fetch(
  //     "https://raw.githubusercontent.com/sjwhitworth/london_geojson/master/london_postcodes.json"
  //   )
  //     .then((response) => response.json())
  //     .then((data) => {
  //       const stuff = data.features;
  //       // for (let i = 0; i < stuff.length; i++) {
  //       //   console.log(stuff[i].geometry.coordinates[0].length);
  //       // }
  //     });
  // }, []);

  let data = new Map<string, MarkerData[]>();
  data.set("NW", [
    {
      coords: { latitude: 51.49493, longitude: 0.14619 },
      title: "LDN",
      description: "Buckingham Palace",
      pinColor: "dodgerblue",
    },
    {
      coords: { latitude: 51.49493, longitude: -0.54519 },
      title: "LDN",
      description: "description2...",
      pinColor: "yellow",
    },
    {
      coords: { latitude: 51.49493, longitude: -0.19919 },
      title: "LDN",
      description: "description3...",
      pinColor: "green",
    },
  ]);

  data.set("SW", [
    {
      coords: { latitude: 51.49493, longitude: 0.14619 },
      title: "LDN",
      description: "Buckingham Palace",
      pinColor: "dodgerblue",
    },
    {
      coords: { latitude: 51.49493, longitude: -0.54519 },
      title: "LDN",
      description: "description2...",
      pinColor: "yellow",
    },
    {
      coords: { latitude: 51.49493, longitude: -0.19919 },
      title: "LDN",
      description: "description3...",
      pinColor: "green",
    },
  ]);

  const markerData = data.get("NW");

  const [centerPointZoom, setCenterPointZoom] = useState<CenterPointZoom>({
    latitude: 51.507351,
    longitude: -0.127758,
    latitudeDelta: 0.715,
    longitudeDelta: 0.7121,
  });

  data.forEach((key, value) => {
    console.log(key + ": " + value);
  });

  console.log(data);

  return (
    <View style={styles.container}>
      <Text>Map Page</Text>
      <View style={styles.separator} />
      <TouchableOpacity>
        <Button
          title="Change Focus & Zoom"
          onPress={() =>
            setCenterPointZoom({
              latitude: 51.49493,
              longitude: -0.19919,
              latitudeDelta: 0.315,
              longitudeDelta: 0.3121,
            })
          }
        />
      </TouchableOpacity>
      {/* Search Bar goes here.. */}
      <View style={styles.mapContainer}>
        <MapView
          provider={PROVIDER_GOOGLE}
          style={styles.map}
          region={centerPointZoom}
          zoomEnabled
          zoomTapEnabled
        >
          {/* Display Markers */}
          {markerData?.map((markerObj, index) => {
            return (
              <Marker
                key={`${index}`}
                coordinate={markerObj.coords}
                title={markerObj.title}
                description={markerObj.description}
                pinColor={markerObj.pinColor}
              />
            );
          })}
        </MapView>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: "80%",
    backgroundColor: "blue",
  },
  mapContainer: {
    // flex: 1,
    height: 400,
    width: "100%",
    // justifyContent: "flex-end",
    // alignItems: "center",
  },
  map: {
    // ...StyleSheet.absoluteFillObject,
    flex: 1,
  },
});

export default MapPage;
